import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  FaStore, FaShoppingBag, FaUsers, FaChartLine, 
  FaStar, FaArrowRight, FaPlay, FaCheck, FaShieldAlt, 
  FaBolt 
} from 'react-icons/fa';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import PricingCard from '../components/PricingCard';
import './LandingPage.css';

const LandingPage = () => {
  const [currentTestimonial, setCurrentTestimonial] = useState(0);
  const [isVisible, setIsVisible] = useState({});

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            setIsVisible(prev => ({ ...prev, [entry.target.id]: true }));
            observer.unobserve(entry.target);
          }
        });
      },
      { threshold: 0.1 }
    );

    document.querySelectorAll('.fade-section').forEach(el => {
      observer.observe(el);
    });

    return () => observer.disconnect();
  }, []);

  // Pricing plans
  const pricingPlans = [
    {
      name: "الباقة الأساسية",
      price: "49",
      period: "شهرياً",
      description: "مثالية للاستخدام الشخصي",
      features: [
        "10GB مساحة تخزين",
        "متجر واحد",
        "دعم بالبريد الإلكتروني",
        "تصاميم أساسية",
        "تقارير بسيطة"
      ]
    },
    {
      name: "الباقة الاحترافية",
      price: "99",
      period: "شهرياً",
      description: "مثالية للفرق الصغيرة",
      features: [
        "50GB مساحة تخزين",
        "متاجر غير محدودة",
        "دعم على مدار الساعة",
        "تصاميم متقدمة",
        "تحليلات وتقارير",
        "API متقدم"
      ]
    },
    {
      name: "الباقة المتقدمة",
      price: "199",
      period: "شهرياً",
      description: "مثالية للمؤسسات",
      features: [
        "مساحة تخزين غير محدودة",
        "متاجر غير محدودة",
        "دعم مخصص",
        "تصاميم مخصصة",
        "تحليلات متقدمة",
        "وصول API كامل",
        "مدير حساب مخصص"
      ]
    }
  ];

  // Features
  const features = [
    { icon: FaStore, title: "متاجر إلكترونية احترافية", description: "أنشئ متجرك الإلكتروني الخاص بسهولة مع تصميم عصري ومتجاوب", color: "from-purple-600 to-purple-800" },
    { icon: FaShoppingBag, title: "إدارة متقدمة للمنتجات", description: "أضف وأدر منتجاتك بسهولة مع تحكم كامل في المخزون والأسعار", color: "from-blue-600 to-blue-800" },
    { icon: FaUsers, title: "إدارة العملاء والطلبات", description: "تتبع طلباتك وإدارة عملائك بفعالية من لوحة تحكم واحدة", color: "from-green-600 to-green-800" },
    { icon: FaChartLine, title: "تحليلات وتقارير مفصلة", description: "احصل على رؤى عميقة حول أداء متجرك ومبيعاتك", color: "from-orange-600 to-orange-800" },
