import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { FaStore, FaShoppingBag, FaUser, FaSearch, FaBars, FaTimes, FaHeart, FaShoppingCart } from 'react-icons/fa';

const Navbar = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <nav className={`fixed top-0 w-full z-50 transition-all duration-300 ${
      isScrolled ? 'glass shadow-lg' : 'bg-transparent'
    }`}>
      <div className="container mx-auto px-6 py-4">
        <div className="flex items-center justify-between">
          {/* Logo */}
          <Link to="/" className="flex items-center space-x-2 space-x-reverse">
            <div className="w-10 h-10 bg-gradient-to-r from-purple-600 to-purple-800 rounded-lg flex items-center justify-center animate-pulse">
              <FaStore className="text-white text-xl" />
            </div>
            <span className="text-2xl font-bold text-white">Home Craft</span>
          </Link>

          {/* Right Side */}
          <div className="hidden md:flex items-center space-x-4 space-x-reverse">
            {/* Search Bar */}
            <div className="relative">
              <input
                type="text"
                placeholder="بحث..."
                className="bg-white/20 backdrop-blur-sm text-white placeholder-purple-200 px-4 py-2 pr-10 rounded-full w-64 focus:outline-none focus:ring-2 focus:ring-purple-400"
              />
              <FaSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-purple-200" />
            </div>

            {/* User Actions */}
            {user ? (
              <div className="flex items-center space-x-3 space-x-reverse">
                <Link to="/dashboard" className="relative">
                  <div className="w-10 h-10 bg-gradient-to-r from-purple-600 to-purple-800 rounded-full flex items-center justify-center">
                    <FaUser className="text-white" />
                  </div>
                </Link>
                <button
                  onClick={handleLogout}
                  className="bg-white/20 backdrop-blur-sm text-white px-4 py-2 rounded-full hover:bg-white/30 transition"
                >
                  تسجيل الخروج
                </button>
              </div>
            ) : (
              <div className="flex items-center space-x-3 space-x-reverse">
                <Link
                  to="/login"
                  className="bg-white/20 backdrop-blur-sm text-white px-4 py-2 rounded-full hover:bg-white/30 transition"
                >
                  تسجيل الدخول
                </Link>
                <Link
                  to="/register"
                  className="bg-white text-purple-700 px-4 py-2 rounded-full hover:bg-purple-50 transition"
                >
                  إنشاء حساب
                </Link>
              </div>
            )}
          </div>

          {/* Mobile Menu Button */}
          <button
            className="md:hidden text-white"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            {isMenuOpen ? <FaTimes size={24} /> : <FaBars size={24} />}
          </button>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="md:hidden mt-4 glass rounded-2xl p-6 animate-slideInRight">
            <div className="flex flex-col space-y-4">
              <Link to="/" className="text-white hover:text-purple-200 transition" onClick={() => setIsMenuOpen(false)}>
                الرئيسية
              </Link>
              <Link to="/stores" className="text-white hover:text-purple-200 transition" onClick={() => setIsMenuOpen(false)}>
                المتاجر
              </Link>
              {user && (
                <>
                  <Link to="/dashboard" className="text-white hover:text-purple-200 transition" onClick={() => setIsMenuOpen(false)}>
                    لوحة التحكم
                  </Link>
                  <Link to="/products" className="text-white hover:text-purple-200 transition" onClick={() => setIsMenuOpen(false)}>
                    المنتجات
                  </Link>
                </>
              )}
              <div className="pt-4 border-t border-white/20">
                {user ? (
                  <button
                    onClick={handleLogout}
                    className="w-full bg-white/20 backdrop-blur-sm text-white px-4 py-2 rounded-full hover:bg-white/30 transition"
                  >
                    تسجيل الخروج
                  </button>
                ) : (
                  <div className="space-y-2">
                    <Link
                      to="/login"
                      className="block w-full bg-white/20 backdrop-blur-sm text-white px-4 py-2 rounded-full hover:bg-white/30 transition text-center"
                      onClick={() => setIsMenuOpen(false)}
                    >
                      تسجيل الدخول
                    </Link>
                    <Link
                      to="/register"
                      className="block w-full bg-white text-purple-700 px-4 py-2 rounded-full hover:bg-purple-50 transition text-center"
                      onClick={() => setIsMenuOpen(false)}
                    >
                      إنشاء حساب
                    </Link>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
